// Extract the JSON block inside the triple backticks
const jsonMatch = rawOutput.match(/```json\s*([\s\S]*?)\s*```/);

if (!jsonMatch || jsonMatch.length < 2) {
  throw new Error("Failed to extract JSON from the string.");
}

// Parse the extracted JSON string
const parsed = JSON.parse(jsonMatch[1]);

// Extract fields
const patient = parsed.patientDetails;

// Extract the email subject
const emailSubject = parsed.emailSubject || "";

return [
  {
    json: {
      name: patient.name || "",
      age: patient.age || "",
      whatsapp: patient.whatsapp || "",
      email: patient.email || "",
      reasonForVisit: patient.reasonForVisit || "",
      dateToVisit: patient["Date to Visit"] || "",
      timeToVisit: patient["Time to Visit"] || "",
      summary: parsed.summary || "",
      emailSubject: emailSubject || "Call Details (2Care)"// Include the email subject here
